        "url": "https://api.github.com/users/sylvanaar",
        "html_url": "https://github.com/sylvanaar",
        "followers_url": "https://api.github.com/users/sylvanaar/followers",
        "following_url": "https://api.github.com/users/sylvanaar/following{/other_user}",
        "gists_url": "https://api.github.com/users/sylvanaar/gists{/gist_id}",
        "starred_url": "https://api.github.com/users/sylvanaar/starred{/owner}{/repo}",
        "subscriptions_url": "https://api.github.com/users/sylvanaar/subscriptions",
        "organizations_url": "https://api.github.com/users/sylvanaar/orgs",
        "repos_url": "https://api.github.com/users/sylvanaar/repos",
        "events_url": "https://api.github.com/users/sylvanaar/events{/privacy}",
